'use strict';

var forestServerRequester = require('./forest-server-requester');
var logger = require('./logger');

function GoogleAuthorizationFinder(renderingId, forestToken, environmentSecret, twoFactorRegistration) {
  this.perform = function () {
    var url = '/liana/v2/renderings/' + renderingId + '/google-authorization';

    if (twoFactorRegistration) {
      url += '?two-factor-registration=' + twoFactorRegistration;
    }

    return forestServerRequester.perform(url, environmentSecret, null, { 'forest-token': forestToken }).then(function (result) {
      return result.data.attributes;
    }).catch(function (error) {
      logger.error(error);
      throw new Error();
    });
  };
}

module.exports = GoogleAuthorizationFinder;