'use strict';

exports.find = function (data, path) {
  if (data && path) {
    var keys = path.split('.');
    // eslint-disable-next-line consistent-return
    keys.forEach(function (key) {
      if (key in data && data[key]) {
        data = data[key];
      } else {
        return null;
      }
    });
    return data;
  }
  return null;
};